var zooKeeperAccess = require ( '../zooKeeperAccess.js' );

test('create node on path /skypath/environment/dev/zookeeperAccess/test/val3', async () => {
    await expect(zooKeeperAccess.createNode("/skypath/environment/dev/zookeeperAccess/test/val3")).resolves.toBe(0)})
test('set value test3 on path /skypath/environment/dev/zookeeperAccess/test/val3', async () => {
    await expect(zooKeeperAccess.setNodeData("/skypath/environment/dev/zookeeperAccess/test/val3", "test3")).resolves.toBe(0)})
test('get children of /skypath/environment/dev/zookeeperAccess', async () => {
    await expect(zooKeeperAccess.getChildren("/skypath/environment/dev/zookeeperAccess")).resolves.toEqual(["test"])})
test('get value test3 on path /skypath/environment/dev/zookeeperAccess/test/val3', async () => {
    await expect(zooKeeperAccess.getNodeData("/skypath/environment/dev/zookeeperAccess/test/val3")).resolves.toBe("test3")})
test('delete value test3 on path /skypath/environment/dev/zookeeperAccess/test/val3', async () => {
    await expect(zooKeeperAccess.deleteNode("/skypath/environment/dev/zookeeperAccess/test/val3")).resolves.toBe(0)})
