var Ajv = require('ajv');
var ajv = new Ajv();
const skypathLogSchema = require('../resources/objects/SkypathLog.schema.json');

function validateLoggingMessage(fullLogMessage) {
    let validate = ajv.compile(skypathLogSchema);
    let validation = { isValid: validate(fullLogMessage), errorDetails: validate.errors };
    return validation;
}

module.exports = {
    validateLoggingMessage,
    skypathLogSchema
}